import Page from '../layouts/Main';

import SubHeader from '../components/SubHeader';


class About extends React.Component {
	render() {
		let message = "Exsender is developed to save time, gas and energy consumption on the blockchain. Token distribution has never been this simplified, sending token to thousands of addresses consumes lots of gas and time. <br/> Exsender automate this process by bundling hundreds of transfer in one single transaction.";
		return (
			<Page>
				<SubHeader />
				<section className="halfpage-height container">
					<h3 className="is-size-5">About Exsender</h3>

					<p dangerouslySetInnerHTML={{__html: message}}></p>


				</section>
			</Page>
		)
	}
}

export default About;
