import Page from '../layouts/Main';
import {SingleInputField, MultipleInputFields} from '../components/InputField';

class TokenDistributor extends React.Component {
	constructor(props) {
		super(props);

	}
	alertValue() {
		alert('hi');
	}
	formFields() {
		return [
				{
					type: 'select', 
					name:"with-amount", 
					classList: "control",
					change: this.alertValue,
					options:[
						{
							name:"orange juicy",
							value:"orange"
						},
						{
							name:"apple juicy",
							value:"apple"
						},
						{
							name:"lemon juicy",
							value:"lemon"
						}
					]
				},
				{
					type: 'text', 
					classList: "input",

					name:"with-amount", 
					list:"with-amount-list", 
					blur: this.alertValue,
					value: "orange",
					options:[
						{
							name:"orange juicy",
							value:"orange"
						},
						{
							name:"apple juicy",
							value:"apple"
						},
						{
							name:"lemon juicy",
							value:"lemon"
						}
					]
				},
				{
					type: 'text', 
					classList: "input",
					name:"with-amount", 
					placeholder: "text field"
				},
				{
					type: 'number', 
					classList: "input",
					name:"witamount", 
					placeholder: "enter a number",
				},
				{
					type: "checkbox",
					name: "diffAmount",
					placeholder: "Same Amount"
				},
				{
					type: "checkbox",
					name: "diffAmount",
					placeholder: "Different Amount"
				},
				{
					type: "radio",
					name: "diffAmount",
					placeholder: "Same Amount"
				},
				{
					type: "radio",
					name: "diffAmount",
					placeholder: "Different Amount"
				}
			]
	}
	render() {
		return (

			<Page>
				<section>
					<div className="container pl-4 pr-4 mt-4 pt-2 pb-2">
						<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
						<div className="columns">
							<form className="">
								<MultipleInputFields 
									inputList={this.formFields()}
								/>
							</form>	
						</div>

					</div>
				</section>
			</Page>	
		)
	}
}

export default TokenDistributor;
