import FileInput from '../components/FileInput';
import { SingleInputField, MultipleInputFields } from '../components/InputField';
import { Web3ConnectionHandler } from '../components/web3-connect';

let xplAddress = "0x2d213012e19f1433cb394ae6faba1c6a71ad1690";

class TokenDistributor extends React.Component {
	constructor(props) {
		super(props);
		this.initialAmount = {
			tokenAmountClassList: "input is-large",
			tokenAmountPlaceholder: "Enter Amount",
			tokenAmountType: "text"
		}
		this.state = {
			isUnifiedAmount: true, 
			isWeb3Browser: false, 
			web3BrowserConnected: false,
			isProcessing: false,
			contractAddress: "",
			contractDecimals: "",
			contractName: "",
			contractSymbol: "",
			addressBalance: "",
			distributorContractAllowance: "",
			selectedFileName: "No file selected",
			textAreaWithAddresses: "",

			tokenAmount: "",
			...this.initialAmount

		};

		this.toggleAmount = this.toggleAmount.bind(this);
		this.unlockBrowser = this.unlockBrowser.bind(this);
		this.handleBlur = this.handleBlur.bind(this);
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleReset = this.handleReset.bind(this);

		// this.fileInput = React.createRef();
	}

	toggleAmount() {
		if (this.state.isUnifiedAmount) {
			this.setState({
				isUnifiedAmount: false,
				tokenAmountClassList: "textarea",
				tokenAmountPlaceholder: "Comma or whitespace separated amount in order of addresses \n240, 50, 100, 10, 75, 13, 5",
				tokenAmountType: "textarea"
			});
		} else {
			this.setState({
				isUnifiedAmount: true,
				...this.initialAmount
			});
		}

	}

	unlockBrowser(){
		let self = this;
		Web3ConnectionHandler.enableWeb3Browser()
		.then(function() {
			self.setState({web3BrowserConnected: true});
		})
		.catch(function(err){
			console.log(err)
		})
	}
	handleSubmit(e) {
		e.preventDefault();
		console.log(this.state);
	}

	async handleBlur(e) {
		let {name, value} = e.target;
		let self = this;
		self.setState({[name]: value});

		if ( name == "contractAddress" ) {
			if( value.length >= 40 ) {
				self.setState({isProcessing: true});

				try {
					let contractName = await Web3ConnectionHandler.getContractProp(value, 'name');

					let contractSymbol = await Web3ConnectionHandler.getContractProp(value, 'symbol');

					let contractDecimals = (await Web3ConnectionHandler.getContractProp(value, 'decimals')).toNumber();

					let addressBalance = ((await Web3ConnectionHandler.getContractProp(value, 'balanceOf', Web3ConnectionHandler.checkBrowserCompatibility().selectedAddress)).toNumber() / Math.pow(10, contractDecimals));

					let distributorContractAllowance = ((await Web3ConnectionHandler.getContractProp(value, 'allowance', Web3ConnectionHandler.checkBrowserCompatibility().selectedAddress, xplAddress)) / Math.pow(10, contractDecimals)).toString();

					// update state
					self.setState({
						contractName,
						contractSymbol,
						contractDecimals,
						addressBalance,
						distributorContractAllowance,
					});

				} catch(err) {

					console.log("Invalid contract Address / Network Error");
					self.setState({contractAddress: ""});

				} finally {

					self.setState({isProcessing: false});
				}
			} else {
				self.emptyContractProps();
			}
		}
	}

	handleChange(e) {
		let {name, value} = e.target;
		let self = this;		
		if (name == "uploadFileInput") {
			let files = document.getElementById(name).files;

			console.log(files);

			if(files.length > 0 ) {
				self.setState({selectedFileName: files[0].name});
			}
		} 
		// let self = this;
		// self.setState({[name]: value});
	}

	emptyContractProps() {
		this.setState({
			contractAddress: "",
			contractName: "",
			contractDecimals: "",
			contractSymbol: "",
			addressBalance: "",
			selectedFileName: "",
			distributorContractAllowance: "",
		});
	}

	handleReset(e) {
		// console.log(e)
		// e.preventDefault();
		this.setState({
			isUnifiedAmount: true,
			contractAddress: "",
			contractName: "",
			contractDecimals: "",
			contractSymbol: "",
			addressBalance: "",
			distributorContractAllowance: "",
			selectedFileName: "",
			tokenAmount: "",
			textAreaWithAddresses: "",
			...this.initialAmount
		});
	}

	distributorFormFields() {
		let formFields = {};
			formFields.contractAddress = {
				type: "text",
				name: "contractAddress",
				placeholder: "Contract Address",
				classList: "input is-large",
				value: this.state.contractAddress,
				blur: this.handleBlur
			};
			formFields.contractDecimals = {
				type: "text",
				name: "contractDecimals",
				placeholder: "Decimals",
				classList: "input is-medium",
				readonly: true,
				value: this.state.contractDecimals
			};
			formFields.addressBalance = {
				type: "text",
				name: "addressBalance",
				placeholder: "Balance",
				classList: "input is-medium",
				readonly: true,
				value: this.state.addressBalance
			};
			formFields.distributorContractAllowance = {
				type: "text",
				name: "distributorContractAllowance",
				placeholder: "Allowance",
				classList: "input is-medium",
				readonly: true,
				value: this.state.distributorContractAllowance

			};
			/*formFields.unifiedAmount = {
				type: "text",
				name: "unifiedAmount",
				placeholder: "Amount of token",
				classList: "input is-large",
				value: this.state.unifiedAmount,
				blur: this.handleBlur

			};*/
			formFields.amountController = [
				{
					type: "radio",
					name: "amountController",
					placeholder: "Unified amount",
					checked: true,
					change: this.toggleAmount
				},
				{
					type: "radio",
					name: "amountController",
					placeholder: "Split amount",
					change: this.toggleAmount
				}
			];
			formFields.uploadFileInput = {
				name: "uploadFileInput",
				change: this.handleChange,
				label: "Select a file (optional)",
				fileName: this.state.selectedFileName
			}
			formFields.tokenAmount = {
				type: this.state.tokenAmountType,
				name: "tokenAmount",
				classList: this.state.tokenAmountClassList,
				value: this.state.tokenAmount,
				placeholder: this.state.tokenAmountPlaceholder,
				blur: this.handleBlur

			}
			formFields.textAreaWithAddresses = {
				type: "textarea",
				name: "textAreaWithAddresses",
				classList: "textarea",
				value: this.state.textAreaWithAddresses,
				placeholder: "Comma or whitespace separated addresses\n0x138e326c28114f65202b4ed82a1eecdf81451e09, 0xca353c48ab32b8baba846477d28254309be5e7c6",
				blur: this.handleBlur

			}

		return formFields;
	}

	componentDidMount() {
		if (Web3ConnectionHandler.checkBrowserCompatibility()) {
			this.setState({isWeb3Browser: true});
			this.unlockBrowser();
		}
	}

	render() {
		if (this.state.web3BrowserConnected && this.state.isWeb3Browser) {
			return (
				<Page>
					<section className="halfpage-height">
						<div className="container pl-4 pr-4 mt-4 pb-4">
							<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
							<div className="columns center-align">
								<form className="column is-8" onSubmit={this.handleSubmit} onReset={this.handleReset}>
									<h2 className="subtitle is-size-4">Token Information</h2>
									<div className="field">
										<SingleInputField
											input={this.distributorFormFields().contractAddress}
											classList="control"
										/>
									</div>
									<div className="columns">
										<div className="column">
											<div className="field">
												<SingleInputField
													input={this.distributorFormFields().contractDecimals}
													classList="control"
												/>	
											</div>
										</div>
										<div className="column">
											<div className="field">
												<SingleInputField
													input={this.distributorFormFields().addressBalance}
													classList="control"
												/>	
											</div>
										</div>

										<div className="column">
											<div className="field">
												<SingleInputField
													input={this.distributorFormFields().distributorContractAllowance}
													classList="control"
												/>	
											</div>
										</div>


									</div>
									<h2 className="subtitle is-size-4 pt-2">Transaction details</h2>

									<div className="field">
										<SingleInputField
											input={this.distributorFormFields().amountController[0]}
										/>
										&nbsp;
										<SingleInputField
											input={this.distributorFormFields().amountController[1]}
										/>
									</div>
									<div className="field">
										<FileInput
											{...this.distributorFormFields().uploadFileInput}
										/>
									</div>
									<div className="field">
										<SingleInputField
											input={this.distributorFormFields().tokenAmount}
											classList="control"
										/>
									</div>

									<div className="field">
										<SingleInputField
											input={this.distributorFormFields().textAreaWithAddresses}
											classList="control"
										/>
									</div>
									<div className="field">
										<div className="control buttons">
											<button 
												className="button is-link is-large"
												type="submit"
												disabled={null}
											>
												Send Transaction
												</button>
											<button 
												className="button is-danger is-large"
												type="reset"

											>
												Reset
											</button>
										</div>
									</div>
								</form>	
								<div className="column">
									<p>
										<b>Selected Address</b><br/>
										<span>{Web3ConnectionHandler.checkBrowserCompatibility().selectedAddress}</span>
									</p>
									<p>
										<b>Network</b><br/>
										<span className={`tag is-medium is-${Web3ConnectionHandler.connectedNetwork().colour || "danger"}`}>
											{Web3ConnectionHandler.connectedNetwork().name || "Unknown Network"}
										</span>
									</p>
									<p>
										<b> Target Contract </b><br/>
										<span> {this.state.contractAddress || "Not a valid contract address"} </span><br/>

										<span> 
											<b>Name: </b> {this.state.contractName || "Not available"} <br/>
											<b>Symbol: </b> {this.state.contractSymbol || "Not available"} 
										</span>
									</p>

									{ this.state.isProcessing &&
									<p>
										<button className="button is-text is-link is-large is-loading">loading</button>
									</p>
									}
								</div>
							</div>			
						</div>
					</section>
				</Page>	
			)
		} else if (this.state.isWeb3Browser) {
			return (
				<LockedWeb3Browser>
					<button 
						className="button is-warning is-large"
						onClick={this.unlockBrowser}
						>
					Unlock Metamask
					</button>
				</LockedWeb3Browser>
			)
		} else {
			return <NoWeb3Browser />
		}
	}
}


function LockedWeb3Browser(props) {
	return (
		<Page>
			<section className="halfpage-height">
				<div className="container pl-4 pr-4 mt-4 pb-4">
					<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
					<div className="columns center-align">
						<div className="column pt-4 mt-4">
							<p className="is-size-4"> Please unlock Metamask to continue.</p><br/>
							{props.children}
						</div>
						<div className="column">
						</div>
					</div>			
				</div>
			</section>
		</Page>	
	)
}

function NoWeb3Browser() {
	return (
			<Page>
				<section className="halfpage-height">
					<div className="container pl-4 pr-4 mt-4 pb-4">
						<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
						<div className="columns">
							<div className="column center-align mt-4 pt-4">
								<p className="is-size-4"> This feature requires Metamask to be installed.</p>
								<a 
									className="button is-danger is-large"
									href="https://metamask.io"
									target="_blank"
									>
								Download Metamask
								</a>
							</div>
							<div className="column">
							</div>
						</div>			
					</div>
				</section>
			</Page>	
		)
}

export default TokenDistributor;
