import Page from '../layouts/Main';


function BulmaFormInputField(props) {
	let inputFields = props.inputList.map((input, index) => {
		if (input.type == "textarea"){

		} else if (input.type == "select" && input.option) {
			let options = [];

			if(input.options) {
				for( let option of input.options) {
					options.push(<option value={option} key={option}> {option} </option>);
				}

			}
			return (
				<div key={index} className={props.classList || ""}>
					<div className="select">
						<select name={input.name || ""} id={input.id || input.name || ""}>
							{options}
						</select>
					</div>
				</div>
			);

		} else {
			let options = [];
			if(input.options) {
				for( let o of input.options) {
					options.push(<option value={o} key={o} />);
				}

			}

			return (
				<div key={index} className={props.classList || ""}>
					<input 
						className={input.classList || ""} 
						type={input.type || "text"} 
						placeholder={input.placeholder || ""} 
						name={input.name || ""} 
						id={input.id || input.name || ""} />

					<datalist id={input.name || ""}>
						{options}
					</datalist>
				</div>
			);
		}
	});
}

class TokenDistributor extends React.Component {
	constructor(props) {
		super(props);

	}


	render() {
		return (

			<Page>
				<section>
					<div className="container pl-4 pr-4 mt-4 pt-2 pb-2">
						TokenDistributor
					</div>
				</section>
			</Page>	
		)
	}
}

export default TokenDistributor;
