import Page from '../layouts/Main';


function SingleInputField(props) {
		let input = props.input;
		if (input.type == "radio" || input.type == "checkbox") {
			return (
				<label className={input.type}>
					<input 
						type={input.type}
						name={input.name}
						id={input.id || null}
						onChange={input.change || null}
						onClick={input.click || null}
					/>
					{input.placeholder}
				</label>
			)

		} else if (input.type == "textarea"){
			return (
				<textarea
					className="textarea"
					placeholder={input.placeholder || null}
					onChange={input.change || null}
					name={input.name || null}
					id={input.id || null}
					defaultValue={input.value || null}
				></textarea>
			)


		} else if (input.type == "select" && input.options) {
			let options = [];

			if(input.options.length) {
				options.push(<option key="-1" disabled> Select an option </option>);

				for( let option of input.options) {
					options.push(<option value={option.value} key={option.value}> {option.name} </option>);
				}

				return (
					<div className="select" defaultValue={input.value}>
						<select 
							name={input.name || null} 
							id={input.id || null} 
							defaultValue={input.value || null}
							onChange={input.change || null}
						>
							{options}

						</select>
					</div>
				);
			}
			return null

		} else {
			let options = [];
			if(input.options && input.list) {
				for( let option of input.options) {
					options.push(<option value={option.value} key={option.value} />);
				}

			}

			return (
				<div>
					<input 
						className={input.classList || null} 
						type={input.type || "text"} 
						placeholder={input.placeholder || null} 
						name={input.name || null}
						onBlur={input.blur || null}
						onFocus={input.focus || null}
						onChange={input.change || null}
						list={input.list || null} 
						id={input.id || null} 
						defaultValue={input.value || null} 
					/>
					<datalist id={input.list}>{options}</datalist>
				</div>
			);
		}
}

function MultipleInputFields(props) {
	let inputFields = props.inputList.map((input, index) => {
		return (
			<div key={index}>
				<SingleInputField
					input={input}
				/>
			</div>
		)
	});
	return inputFields;
}


class TokenDistributor extends React.Component {
	constructor(props) {
		super(props);

	}
	alertValue() {
		alert('hi');
	}
	formFields() {
		return [
				{
					type: 'select', 
					name:"with-amount", 
					classList: "control",
					value: "apple",
					change: this.alertValue,
					options:[
						{
							name:"orange juicy",
							value:"orange"
						},
						{
							name:"apple juicy",
							value:"apple"
						},
						{
							name:"lemon juicy",
							value:"lemon"
						}
					]
				},
				{
					type: 'text', 
					classList: "input",

					name:"with-amount", 
					list:"with-amount-list", 
					value: "orange",
					options:[
						{
							name:"orange juicy",
							value:"orange"
						},
						{
							name:"apple juicy",
							value:"apple"
						},
						{
							name:"lemon juicy",
							value:"lemon"
						}
					]
				},
				{
					type: 'text', 
					classList: "input",
					name:"with-amount", 
					placeholder: "text field"
				},
				{
					type: 'number', 
					classList: "input",
					name:"witamount", 
					placeholder: "enter a number",
				},
				{
					type: "checkbox",
					name: "diffAmount",
					placeholder: "Same Amount"
				},
				{
					type: "checkbox",
					name: "diffAmount",
					placeholder: "Different Amount"
				},
				{
					type: "radio",
					name: "diffAmount",
					placeholder: "Same Amount"
				},
				{
					type: "radio",
					name: "diffAmount",
					placeholder: "Different Amount"
				}
			]
	}
	render() {
		return (

			<Page>
				<section>
					<div className="container pl-4 pr-4 mt-4 pt-2 pb-2">
						TokenDistributor
						<form>
							<MultipleInputFields 
								inputList={this.formFields}
							/>
						</form>
					</div>
				</section>
			</Page>	
		)
	}
}

export default TokenDistributor;
