function DistributionStat(props) {
	return (
			<div className="stat is-size-6">
				<h3><b> Statistics </b></h3>

				<p>
					Amount counts: {props.totalAmount || 0} <br/>
					Addresses counts: {props.totalAddresses || 0} 
				</p>
			</div>
	)
}

function TransactionMonitor(props) {
	if (!props.monitorArray instanceof Array || props.monitorArray.length < 1) {
		return null;
	} 

	let transactionList = props.monitorArray.map( (tx, i) => {
		let status;
		if (tx.status == "pending") {

			status = <p key={i}>{i+1}. <i>waiting for broadcast</i> <span className="has-text-link fas fa-exclamation-circle"></span></p>;

		} else if (tx.status == "processing") {

			status = <p key={i}>{i+1}. <i>{tx.hash}</i> <span className="has-text-primary fas fa-spinner fa-spin"></span></p>;

		} else if (tx.status == "success") {

			status = <p key={i}>{i+1}. <i>{tx.hash}</i> <span className="has-text-success fas fa-check-circle"></span></p>;

		} else {

			status = <p key={i}>{i+1}. <i>{tx.hash}</i> <span className="has-text-danger fas fa-times-circle"></span> <button className="button is-danger is-small"> Retry </button></p>;
		}

		return status;
	} )

	return (
		<div className="custom-overlay">
			<i 
				className="is-pulled-right is-small button fas fa-times"
				onClick={props.onClick}
			>
			</i>
			{transactionList}
		</div>
	)
}

