import Page from '../layouts/Main';

import SubHeader from '../components/SubHeader';


class Faq extends React.Component {
	questionsAndAnswers() {
		return [
			{
				q: "How do I use the Token Distributor?",
				a: "How do I use the Token Distributor"
			},
			{
				q: "How to import addresses and or amount from file?",
				a: ""
			},
			{
				q: "What is 'Unified Amount'?",
				a: ""
			},
			{
				q: "What is 'Splitted Amount'?",
				a: ""
			},
			{
				q: "What is the maximum number of addresses?",
				a: ""
			},

			{
				q: "What is 'transfer per transaction'?",
				a: ""
			},
			{
				q: "What is the 'approval transaction'?",
				a: ""
			},
			{
				q: "Do I need to approve before every transaction?",
				a: ""
			},
			{
				q: "Why is my transaction keep failing?",
				a: ""
			},
			{
				q: "Why is the 'approval transaction' failing?",
				a: ""
			},
			{
				q: "What is 'Monitor'?",
				a: ""
			},
			{
				q: "How to retry a failed transaction?",
				a: ""
			},
			{
				q: "How much does it cost to use the app?",
				a: ""
			},
			{
				q: "What is my balance showing zero?",
				a: ""
			},

			{
				q: "Which network is supported?",
				a: ""
			},
			{
				q: "Metamask is very slow, what can I do?",
				a: ""
			},
			{
				q: "Do I need to confirm every transaction on Metamask?",
				a: ""
			},
		]		
	}
	render() {
		return (
			<Page>
				<SubHeader />
				<section className="halfpage-height container">
					<h3 className="is-size-5">Frequently Asked Questions</h3>
					<p>Here, you'll find answers to common questions. Otherwise contact us through our social channel.</p>
				</section>
			</Page>
		)
	}
}

function FaqContainer(props) {

	return (
		<div className="content">
			<h3 className="is-size-6 has-background-light">{props.question}</h3>
			<p className="pt-1 pb-1 pr-1 pl-1">{props.answer}</p>
		</div>
	)
}


export default Faq;
