import Page from '../layouts/Main';


function SingleInputField(props) {
		let input = props.input;
		if (input.type == "radio" || input.type == "checkbox") {
			return (
				<label className={input.type}>
					<input 
						type={input.type}
						name={input.name}
						onChange={input.change || null}
						onClick={input.click || null}
					/>
					{input.placeholder}
				</label>
			)

		} else if (input.type == "textarea"){
			return (
					<textarea
						className="textarea"
						placeholder={input.placeholder || ""}
						onChange={input.change || null}
						name={input.name || ""}
						id={input.id || input.name || ""}
						defaultValue={input.value || ""}
					></textarea>
			)


		} else if (input.type == "select" && input.options) {
			let options = [];

			if(input.options.length) {
				options.push(<option key="-1" disabled> Select an option </option>);

				for( let option of input.options) {
					options.push(<option value={option.value} key={option.value}> {option.name} </option>);
				}

				return (
					<div className="select" defaultValue={input.value}>
						<select 
							name={input.name || ""} 
							id={input.id || input.name || ""} 
							defaultValue={input.value || ""}
							onChange={input.change || null}

						>
							{options}

						</select>
					</div>
				);
			}
			return null

		} else {
			let options = [];
			if(input.options && input.list) {
				for( let option of input.options) {
					options.push(<option value={option.value} key={option.value} />);
				}

			}

			return (
				<div>
					<input 
						className={input.classList || ""} 
						type={input.type || "text"} 
						placeholder={input.placeholder || ""} 
						name={input.name || ""}
						onBlur={input.blur || null}
						onFocus={input.focus || null}
						onChange={input.change || ""}
						list={input.list || ""} 
						id={input.id || input.name || ""} 
						defaultValue={input.value || ""} 
					/>
					<datalist id={input.list}>{options}</datalist>
				</div>
			);
		}
}

function MultipleInputFields(props) {
	let inputFields = props.inputList.map((input, index) => {
		return (
			<div key={index}>
				<SingleInputField
					input={input}
				/>
			</div>
		)
	});
	return inputFields;
}

let formFields = [
	{
		type: 'select', 
		name:"with-amount", 
		classList: "control",
		value: "apple",
		options:[
			{
				name:"orange juicy",
				value:"orange"
			},
			{
				name:"apple juicy",
				value:"apple"
			},
			{
				name:"lemon juicy",
				value:"lemon"
			}
		]
	},
	{
		type: 'text', 
		classList: "input",

		name:"with-amount", 
		list:"with-amount-list", 
		value: "orange",
		options:[
			{
				name:"orange juicy",
				value:"orange"
			},
			{
				name:"apple juicy",
				value:"apple"
			},
			{
				name:"lemon juicy",
				value:"lemon"
			}
		]
	},
	{
		type: 'text', 
		classList: "input",
		name:"with-amount", 
		placeholder: "text field"
	},
	{
		type: 'number', 
		classList: "input",
		name:"witamount", 
		placeholder: "enter a number",
	},
	{
		type: "checkbox",
		name: "diffAmount",
		placeholder: "Same Amount"
	},
	{
		type: "checkbox",
		name: "diffAmount",
		placeholder: "Different Amount"
	},
	{
		type: "radio",
		name: "diffAmount",
		placeholder: "Same Amount"
	},
	{
		type: "radio",
		name: "diffAmount",
		placeholder: "Different Amount"
	}
]

class TokenDistributor extends React.Component {
	constructor(props) {
		super(props);

	}
	alertValue() {
		alert('hi');
	}

	render() {
		return (

			<Page>
				<section>
					<div className="container pl-4 pr-4 mt-4 pt-2 pb-2">
						TokenDistributor
						<form>
							<MultipleInputFields 
								inputList={formFields}
							/>
						</form>
					</div>
				</section>
			</Page>	
		)
	}
}

export default TokenDistributor;
