import Page from '../layouts/Main';

import SubHeader from '../components/SubHeader';


class About extends React.Component {
	render() {
		let message = "Exsender is developed to save time, gas and energy consumption on the blockchain. Token distribution has never been this simplified, sending token to thousands of addresses consumes lots of gas and time. Exsender automate this process by bundling hundreds of transfer in one single transaction. Just drop in addresses, send transaction, sit back, relax and watch.We have distributed to 10k addresses in 30 minutes.";
		return (
			<Page>
				<SubHeader />
				<section className="halfpage-height container">
					<div className="columns">
						<div className="column is-6">
							<h3 className="is-size-5">About Exsender</h3>

							<p dangerouslySetInnerHTML={{__html: message}}></p>
						</div>
					</div>
				</section>
			</Page>
		)
	}
}

export default About;
