import Page from '../layouts/Main';

import { Input, Button, FileInput, TextArea, MultipleCheckBoxes } from '../components/FormElements';
import { LockedWeb3Browser, NoWeb3Browser } from '../components/Web3Status';

import { SpreadsheetHelper } from '../utils/fileHelpers';

import { Web3ConnectionHandler } from '../utils/web3-connect';

const xplAddress = "0x2d213012e19f1433cb394ae6faba1c6a71ad1690";

class TokenDistributor extends React.Component {
	constructor(props) {
		super(props);
		this.contractProps = {
			contractName: "",
			contractDecimals: "",
			contractSymbol: "",
			addressBalance: "",
			distributorContractAllowance: "",
		}
		this.newDistribution = {
			selectedFileName: "No file selected",
			textAreaWithAddresses: "",
			tokenAmount: "",
		}

		this.state = {
			isUnifiedAmount: true, 
			isWeb3Browser: false, 
			web3BrowserConnected: false,
			isProcessing: false,
			contractAddress: "",
			...this.contractProps,
			...this.newDistribution
		};

		this.toggleAmount = this.toggleAmount.bind(this);
		this.unlockBrowser = this.unlockBrowser.bind(this);
		this.handleBlur = this.handleBlur.bind(this);
		this.handleChange = this.handleChange.bind(this);
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleReset = this.handleReset.bind(this);

		// this.fileInput = React.createRef();
	}

	toggleAmount() {
		this.setState({isUnifiedAmount: !this.state.isUnifiedAmount})
	}

	unlockBrowser() {
		let self = this;
		Web3ConnectionHandler.enableWeb3Browser()
		.then(function() {
			self.setState({web3BrowserConnected: true});
		})
		.catch(function(err){
			console.log(err)
		})
	}
	handleSubmit(e) {
		e.preventDefault();
		console.log(this.state);
	}

	async handleBlur(e) {
		let {name, value} = e.target;
		let self = this;
		self.setState({[name]: value});

		if ( name == "contractAddress" ) {
			let startOfAddress = value.substring(0, 2) == "0x";

			let checkBasicAddressFormat = (value.length == 42 &&  startOfAddress) || (value.length == 40 && !startOfAddress);

			if( checkBasicAddressFormat ) {
				self.setState({isProcessing: true});
				try {
					let contractName = await Web3ConnectionHandler.getContractProp(value, 'name');

					let contractSymbol = await Web3ConnectionHandler.getContractProp(value, 'symbol');

					let contractDecimals = (await Web3ConnectionHandler.getContractProp(value, 'decimals')).toNumber();

					let addressBalance = ((await Web3ConnectionHandler.getContractProp(value, 'balanceOf', Web3ConnectionHandler.checkBrowserCompatibility().selectedAddress)).toNumber() / Math.pow(10, contractDecimals)).toString();

					let distributorContractAllowance = ((await Web3ConnectionHandler.getContractProp(value, 'allowance', Web3ConnectionHandler.checkBrowserCompatibility().selectedAddress, xplAddress)) / Math.pow(10, contractDecimals)).toString();

					// update state
					self.setState({
						contractName,
						contractSymbol,
						contractDecimals,
						addressBalance,
						distributorContractAllowance,
						contractInputCssClass: "input is-large is-success"
					});

				} catch(err) {
					console.log("Invalid contract Address / Check network");
					this.emptyContractProps();
				} finally {
					self.setState({isProcessing: false});
				}
			} else {

				this.emptyContractProps();
			}
		}
	}

	handleChange(e) {
		let {name, value} = e.target;
		let self = this;

		if (name == "uploadFileInput") {
			let files = document.getElementById(name).files;

			if(files.length > 0 ) {
				self.setState({selectedFileName: files[0].name});

				SpreadsheetHelper.readFile(files[0])
				.then( json => {
					let fileColumn = this.state.isUnifiedAmount ? ["address"] : ["address", "amount"];
					console.log(json);

					return SpreadsheetHelper.readObjectProperty(json, fileColumn);
				})
				.then( columnsObject => {
					console.log("columnsObject>>>", columnsObject);
				})
				.catch( err = {

				})

			}


		} else {

			self.setState({[name]: value});	
		} 

	}

	emptyContractProps() {
		this.setState({
			contractInputCssClass: "input is-large is-danger",
			...this.contractProps
		});
	}

	handleReset() {
		this.setState({
			contractAddress: "",
			contractInputCssClass: "input is-large",
			...this.newDistribution,
			...this.contractProps
		});
	}

	formFields() {
		return {
			contractAddress: {
				type: "text",
				name: "contractAddress",
				placeholder: "Contract Address",
				className: this.state.contractInputCssClass || "input is-large",
				value: this.state.contractAddress,
				onBlur: this.handleBlur,
				onChange: this.handleChange
			},
			contractDecimals: {
				type: "text",
				name: "contractDecimals",
				placeholder: "Decimals",
				className: "input is-medium",
				readOnly: true,
				value: this.state.contractDecimals
			},
			addressBalance: {
				type: "text",
				name: "addressBalance",
				placeholder: "Balance",
				className: "input is-medium",
				readOnly: true,
				value: this.state.addressBalance
			},
			distributorContractAllowance: {
				type: "text",
				name: "distributorContractAllowance",
				placeholder: "Allowance",
				className: "input is-medium",
				readOnly: true,
				value: this.state.distributorContractAllowance

			},
			amountController: [
				{
					type: "radio",
					name: "amountController",
					placeholder: "Unified amount",
					defaultChecked: true,
					onChange: this.toggleAmount
				},

				{
					type: "radio",
					name: "amountController",
					placeholder: "Split amount",
					onChange: this.toggleAmount
				}
			],
			uploadFileInput: {
				name: "uploadFileInput",
				onChange: this.handleChange,
				label: "Select a file (optional)",
				fileName: this.state.selectedFileName
			},
			tokenAmountInput: {
				type: "text",
				name: "tokenAmount",
				className: "input is-large",
				value: this.state.tokenAmount,
				placeholder: "Amount of token",
				onBlur: this.handleBlur,
				onChange: this.handleChange
			},
			tokenAmountTextArea: {
				name: "tokenAmount",
				value: this.state.tokenAmount,
				onBlur: this.handleBlur,
				onChange: this.handleChange,
				placeholder: "Comma or whitespace separated amount in order of addresses \n240, 50, 100, 10, 75, 13, 5",
				tokenAmountType: "textarea",
			},

			textAreaWithAddresses: {
				name: "textAreaWithAddresses",
				value: this.state.textAreaWithAddresses,
				placeholder: "Comma or whitespace separated addresses\n0x138e326c28114f65202b4ed82a1eecdf81451e09, 0xca353c48ab32b8baba846477d28254309be5e7c6",
				onBlur: this.handleBlur,
				onChange: this.handleChange
			},
			submitButton: {
				type: "submit",
				text: "Send Transaction",
				className: "button is-large is-link",
			},
			resetButton: {
				type: "button",
				text: "Reset",
				className: "button is-medium is-danger",
				onClick: this.handleReset
			}
		}
	}

	componentDidMount() {
		if (Web3ConnectionHandler.checkBrowserCompatibility()) {
			this.setState({isWeb3Browser: true});
			this.unlockBrowser();
		}
	}

	render() {
		if (this.state.web3BrowserConnected && this.state.isWeb3Browser) {
			return (
				<Page>
					<section className="halfpage-height">
						<div className="container pl-4 pr-4 mt-4 pb-4">
							<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
							<div className="columns center-align">
								<form className="column is-8" onSubmit={this.handleSubmit}>
									<h2 className="subtitle is-size-4">Token Information</h2>
									<Input {...this.formFields().contractAddress} />

									<div className="columns">
										<div className="column">
											<Input {...this.formFields().contractDecimals} />
										</div>
										<div className="column">
											<Input {...this.formFields().addressBalance} />
										</div>
										<div className="column">
											<Input {...this.formFields().distributorContractAllowance} />
										</div>
									</div>

									<MultipleCheckBoxes 
										inputArray={this.formFields().amountController}
									/>
									<FileInput
										{...this.formFields().uploadFileInput} 
									/>

									{
										this.state.isUnifiedAmount &&
										<Input {...this.formFields().tokenAmountInput} />
									}
									{
										!this.state.isUnifiedAmount &&
										<TextArea
											{...this.formFields().tokenAmountTextArea} 
										/>
									}

									<TextArea
										{...this.formFields().textAreaWithAddresses} 
									/>

									<div className="field">
										<div className="control buttons">
											<Button
												{...this.formFields().submitButton}
											/>
											<Button
												{...this.formFields().resetButton}
											/>
										</div>
									</div>
								</form>

								<div className="column">
									<p>
										<b>Selected Address</b><br/>
										<span>{Web3ConnectionHandler.checkBrowserCompatibility().selectedAddress}</span>
									</p>
									<p>
										<b>Network</b><br/>
										<span className={`tag is-medium is-${Web3ConnectionHandler.connectedNetwork().colour || "danger"}`}>
											{Web3ConnectionHandler.connectedNetwork().name || "Unknown Network"}
										</span>
									</p>
									<p>
										<b> Target Contract </b><br/>
										<span> {this.state.contractAddress || "Not a valid contract address"} </span><br/>

										<span> 
											<b>Name: </b> {this.state.contractName || "Not available"} <br/>
											<b>Symbol: </b> {this.state.contractSymbol || "Not available"} 
										</span>
									</p>

									{ this.state.isProcessing &&
									<p>
										<button className="button is-text is-link is-large is-loading">loading</button>
									</p>
									}
								</div>
							</div>			
						</div>
					</section>
				</Page>	
			)
		} else if (this.state.isWeb3Browser) {
			return (
				<Page>
					<LockedWeb3Browser>
						<button 
							className="button is-warning is-large"
							onClick={this.unlockBrowser}
							>
						Unlock Metamask
						</button>
					</LockedWeb3Browser>
				</Page>
			)
		} else {
			return (
				<Page>
					<NoWeb3Browser />
				</Page>
			) 
		}
	}
}



export default TokenDistributor;
