import Page from '../layouts/Main';
import { SingleInputField, MultipleInputFields } from '../components/InputField';
import { Web3ConnectionHandler } from '../components/web3-connect';

class TokenDistributor extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			isUnifiedAmount: true, 
			isWeb3Browser: false, 
			web3BrowserConnected: false,
			newDistribution: {
				contractAddress: "",
				contractDecimals: "",
				addressBalance: "",
				distributorContractAllowance: "",
				unifiedAmount: "",
				textAreaWithAmount: "",
				textAreaWithAddresses: ""
			}		
		};

		this.toggleAmount = this.toggleAmount.bind(this);
		this.unlockBrowser = this.unlockBrowser.bind(this);
	}

	toggleAmount() {
		this.setState({isUnifiedAmount: !this.state.isUnifiedAmount});
	}

	unlockBrowser(){
		let self = this;
		Web3ConnectionHandler.enableWeb3Browser()
		.then(function(){
			self.setState({web3BrowserConnected: true});
		})
		.catch(function(err){
			console.log(err)
		})
	}

	distributorFormFields() {
		let formFields = {};
			formFields.contractAddress = {
				type: "text",
				name: "contractAddress",
				placeholder: "Contract Address",
				classList: "input is-large",
				value: this.state.contractAddress
			};
			formFields.contractDecimals = {
				type: "text",
				name: "contractDecimals",
				placeholder: "Decimals",
				classList: "input is-medium",
				readonly: true,
				value: this.state.contractDecimals

			};
			formFields.addressBalance = {
				type: "text",
				name: "addressBalance",
				placeholder: "Balance",
				classList: "input is-medium",
				readonly: true,
				value: this.state.addressBalance
			};
			formFields.distributorContractAllowance = {
				type: "text",
				name: "distributorContractAllowance",
				placeholder: "Allowance",
				classList: "input is-medium",
				readonly: true,
				value: this.state.distributorContractAllowance

			};
			formFields.unifiedAmount = {
				type: "text",
				name: "unifiedAmount",
				placeholder: "Amount of token",
				classList: "input is-large",
				value: this.state.unifiedAmount
			};
			formFields.amountController = [
				{
					type: "radio",
					name: "amountController",
					placeholder: "Unified amount",
					checked: true,
					change: this.toggleAmount
				},
				{
					type: "radio",
					name: "amountController",
					placeholder: "Split amount",
					change: this.toggleAmount
				}
			];
			formFields.textAreaWithAmount = {
				type: "textarea",
				name: "textAreaWithAmount",
				classList: "textarea",
				placeholder: "Comma separated amount in order of addresses \n240, 50, 100, 10, 75, 13, 5"
			}
			formFields.textAreaWithAddresses = {
				type: "textarea",
				name: "textAreaWithAddresses",
				classList: "textarea",
				placeholder: "Comma separated addresses in order of amount\n0x138e326c28114f65202b4ed82a1eecdf81451e09, 0xca353c48ab32b8baba846477d28254309be5e7c6"
			}

		return formFields;
	}

	componentDidMount() {
		if (Web3ConnectionHandler.checkBrowserCompatibility()) {
			this.setState({isWeb3Browser: true});
			this.unlockBrowser();
		}
	}

	render() {
		if (this.state.web3BrowserConnected && this.state.isWeb3Browser) {
			return (
				<Page>
					<section className="halfpage-height">
						<div className="container pl-4 pr-4 mt-4 pb-4">
							<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
							<div className="columns center-align">
								<form className="column is-8">
									<h2 className="subtitle is-size-4">Token Information</h2>
									<div className="field">
										<SingleInputField
											input={this.distributorFormFields().contractAddress}
											classList="control"
										/>
									</div>
									<div className="columns">
										<div className="column">
											<div className="field">
												<SingleInputField
													input={this.distributorFormFields().contractDecimals}
													classList="control"
												/>	
											</div>
										</div>
										<div className="column">
											<div className="field">
												<SingleInputField
													input={this.distributorFormFields().addressBalance}
													classList="control"
												/>	
											</div>
										</div>

										<div className="column">
											<div className="field">
												<SingleInputField
													input={this.distributorFormFields().distributorContractAllowance}
													classList="control"
												/>	
											</div>
										</div>


									</div>
									<h2 className="subtitle is-size-4 pt-2">Transaction details</h2>

									<div className="field">
										<SingleInputField
											input={this.distributorFormFields().amountController[0]}
										/>
										&nbsp;
										<SingleInputField
											input={this.distributorFormFields().amountController[1]}
										/>
									</div>

									{
										this.state.isUnifiedAmount &&
										<div className="field">
											<SingleInputField
												input={this.distributorFormFields().unifiedAmount}
												classList="control"
											/>
										</div>
									}

									{
										!this.state.isUnifiedAmount &&
										<div className="field">
											<SingleInputField
												input={this.distributorFormFields().textAreaWithAmount}
												classList="control"
											/>
										</div>
									}
									<div className="field">
										<SingleInputField
											input={this.distributorFormFields().textAreaWithAddresses}
											classList="control"
										/>
									</div>
									<div className="field">
										<div className="control buttons">
											<button className="button is-link is-large">Send Transaction</button>
											<button className="button is-danger is-large">Reset</button>
										</div>
									</div>
								</form>	
								<div className="column">
								</div>
							</div>			
						</div>
					</section>
				</Page>	
			)
		} else if (this.state.isWeb3Browser) {
			return (
				<Page>
					<section className="halfpage-height">
						<div className="container pl-4 pr-4 mt-4 pb-4">
							<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
							<div className="columns center-align">
								<div className="column pt-4 mt-4">
									<p className="is-size-4"> Please unlock Metamask to continue.</p><br/>
									<button 
										className="button is-warning is-large"
										onClick={this.unlockBrowser}
										>
									Unlock Metamask
									</button>
								</div>
								<div className="column">
								</div>
							</div>			
						</div>
					</section>
				</Page>	
			)
		} else {
			return (
				<Page>
					<section className="halfpage-height">
						<div className="container pl-4 pr-4 mt-4 pb-4">
							<h1 className="is-size-3 has-text-centered">Token Distributor</h1>
							<div className="columns">
								<div className="column center-align mt-4 pt-4">
									<p className="is-size-4"> This feature requires Metamask to be installed.</p>
									<a 
										className="button is-danger is-large"
										href="https://metamask.io"
										target="_blank"
										>
									Download Metamask
									</a>
								</div>
								<div className="column">
								</div>
							</div>			
						</div>
					</section>
				</Page>	
			)
		}
	}
}


export default TokenDistributor;
