export function Input(props) {
		let options = [];
		if(props.options && props.list) {
			for( let option of props.options) {
				options.push(<option value={option.value} key={option.value} />);
			}

		}

		return (
			<div className="field">
				<div className="control">
					<input 
						{...props}
					/>
					{
						options.length > 0 && 
						<datalist id={props.list}>{options}</datalist>
					}
				</div>
			</div>
		);
}

export function Select(props) {
		let options = [];

		if(props.options.length) {
			options.push(<option key="-1" value="" disabled> Select an option </option>);

			for( let option of props.options) {
				options.push(<option value={option.value} key={option.value}> {option.name} </option>);
			}

			return (
				<div className="field">
					<div className="control">
						<div className="select">
							<select 
								name={props.name || null} 
								id={props.id || null}
								value={props.value || ""}
								onChange={props.change || null}
							>
								{options}

							</select>
						</div>
					</div>
				</div>
			);
		}
		return null
}

export function TextArea(props) {
	return (
		<div className="field">
			<div className="control">
				<textarea
					className="textarea"
					placeholder={props.placeholder || null}
					onChange={props.change || null}
					onBlur={props.blur || null}
					name={props.name || null}
					id={props.id || null}
					value={props.value || ""}
				></textarea>
			</div>
		</div>
	)
}

export function CheckBox(props) {
	return (
			<label className={props.type}>
				<input 
					type={props.type}
					name={props.name || null}
					defaultChecked={props.checked || null}
					id={props.id || null}
					disabled={props.disabled || null}
					onChange={props.change || null}
					onClick={props.click || null}
				/>
				 &nbsp; {props.placeholder}
			</label>
		)
}

export function Button(props) {
	return (
		<button
			{...props}
		>
			{props.text}
		</button>
	)
}

export function FileInput(props) {
  return (
    <div className="file is-link has-name">
      <label className="file-label">
        <input className="file-input" type="file" name={props.name} id={props.id || props.name} onChange={props.change} />
        <span className="file-cta">
          <span className="file-icon">
            <i className="fas fa-upload"></i>
          </span>
          <span className="file-label">
            {props.label}
          </span>
        </span>
        <span className="file-name" title={props.fileName}>
          {props.fileName}
        </span>
      </label>
    </div>
  )
}


export function MultipleInputFields(props) {
	let inputFields = props.inputList.map((input, index) => {
		return (
			<div key={index} className={props.classList || null}>
				<SingleInputField
					input={input}
				/>
			</div>
		)
	});
	return inputFields;
}
